import React, { useEffect } from "react";
import { AppBar, IconButton, Menu, MenuItem } from "@mui/material";
import { ReactComponent as Moon } from "../../icons/moon.svg";
import { ReactComponent as Sun } from "../../icons/sun.svg";
import { PageURL } from "../Navigation";
import { Link, useLocation } from "react-router-dom";

interface HeaderProps {
  darkMode: boolean;
  toggleDarkMode: () => void;
}

const projectOptions = ["Software", "Built Environment"];

const Header: React.FC<HeaderProps> = ({ darkMode, toggleDarkMode }) => {
  const [anchorEl, setAnchorEl] = React.useState<null | HTMLElement>(null);
  const handleClick = () => {
    toggleDarkMode();
  };
  const handleOpen = (event: React.MouseEvent<HTMLLIElement>) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  const location = useLocation();
  useEffect(() => {
    const hash = location.hash;
    if (hash) {
      const targetElement = document.querySelector(hash);
      if (targetElement) {
        targetElement.scrollIntoView({ behavior: "smooth" });
      }
    }
  }, [location]);

  return (
    <>
      <AppBar
        position="static"
        style={{
          backgroundColor: darkMode ? "purple" : "orangered",
          flexDirection: "row",
          padding: "0px 10px",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        <h1>Hey There!</h1>
        <Link to={PageURL.About}>
          <MenuItem>About</MenuItem>
        </Link>
        {/* <MenuItem to={PageURL.Skills}>Skills</MenuItem> */}
        <MenuItem onClick={handleOpen}>Projects</MenuItem>
        <IconButton onClick={handleClick}>
          {darkMode ? <Sun /> : <Moon />}
        </IconButton>
      </AppBar>
      <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={handleClose}>
        {/* {projectOptions.map((option) => (
          <MenuItem
            to={`${PageURL.Projects}/${option.toLowerCase().replace(" ", "-")}`}
          >
            {option}
          </MenuItem>
        ))} */}
      </Menu>
    </>
  );
};

export default Header;
